import React from 'react'

const Todo = ({todo, index, deleteTodo, handleCambio}) => {
    return (
        <>
            <div className="list">
                <h3>{todo}</h3>
                 <button className="btn-delete" onClick={() => deleteTodo(index)}>x</button>
                <input type="checkbox" onChange={() => handleCambio(index)}/>
            </div>
        </>
    )
}

export default Todo
