import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
const Todo = ({ todo, index, deleteTodo, handleCambio, updateTodo }) => {
  return (
    <>
      <div className="list">
        <h3>{todo}</h3>
        <button className="btn btn-danger" onClick={() => deleteTodo(index)}>
          x
        </button>
        <button className="btn btn-primary" onClick={() => updateTodo(index)}>
          U
        </button>
        <input type="checkbox" onChange={() => handleCambio(index)} />
        <div className="form-check">
          <input
            className="form-check-input"
            type="checkbox"
            value=""
            id="flexCheckIndeterminate"
          />
          <label class="form-check-label" for="flexCheckIndeterminate">
            tarea
          </label>
        </div>
      </div>
    </>
  );
};

export default Todo;
